import React, { useState } from "react";
import { getDetailByName } from "../../actions";
import { useDispatch, useSelector } from "react-redux";






const SearchBar = ()=>{
    const dispatch = useDispatch();
    const [pokemon,setPokemon] = useState('');
    const pokemons = useSelector((state) => state.pokemons);
    
    let changeInput=(e)=>{
        setPokemon(e.target.value);
    }

    let handlerSubmit=(e)=>{
        e.preventDefault(e)
        const findPokemon = pokemons.find(e => e.name.toLowerCase() === pokemons.toLowerCase());
        if(findPokemon){
            dispatch(getPokemonByName(pokemons))
            setPokemon('')
        }else{
            setPokemon('')
            window.location.href = 'http://localhost:3000/cartel';
            
        }
        
    }
    
    /*let handlerSubmit=(e)=>{
        e.preventDefault(e)
        dispatch(getPokemonByName(pokemon));
        setPokemon('');
    }*/
    

    return(
        <div>
            <form onSubmit={(e)=> handlerSubmit(e)}>
                <input 
                type='text' 
                placeholder='Pokemon...'
                onChange={(e)=> changeInput(e)}
                value={pokemons}
                />
                <button type='submit'>Buscar</button>
            </form>
        </div>
    )

}

export default SearchBar;
