import React from "react";
import { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { postPokemon, getTypes, getPokemon } from '../actions/index';

function validate(input) {
    let errors = {};
    if (input.name === '') {
        errors.name = 'Es necesario un nombre'
    }
    if (input.hp < 0) {
        errors.hp = 'Debe ser mayor a 0'
    }
    if (input.attack < 0) {
        errors.attack = 'Debe ser mayor a 0'
    }
    if (input.defense < 0) {
        errors.defense = 'Debe ser mayor a 0'
    }
    if (input.speed < 0) {
        errors.speed = 'Debe ser mayor a 0'
    }
    if (input.height < 0) {
        errors.height = 'Debe ser mayor a 0'
    }
    if (input.weight < 0) {
        errors.weight = 'Debe ser mayor a 0'
    }
    if (input.types.length === 0) {
        errors.types = 'Debe tener al menos un tipo'
    } else if (!input.image) {
        errors.image = "Please insert an image URL";
    } else if (
        !/(http(s?):)([/|.|\w|\s|-])*\.(?:jpg|gif|png)/.test(input.image)
    ) {
        errors.image = "Please insert a valid image URL";
    }
    return errors;
}

const CreatePokemon = () => {
    const dispatch = useDispatch();
    const history = useNavigate();
    const types = useSelector((state) => state.types);
    const pokemons = useSelector((state) => state.pokemons);
    const [input, setInput] = useState({ name: "", hp: "", attack: "", defense: "", speed: "", height: "", weight: "", types: [] });
    const [errors, setErrors] = useState({});
    const [button, setButton] = useState(true);
    const [typ, setTemps] = useState([]);

    useEffect(() => {
        dispatch(getTypes());
        dispatch(getPokemon())
        // eslint-disable-next-line
    }, [])


    function handleOnChange(e) {
        setInput({
            ...input,
            [e.target.name]: e.target.value
        });
        setErrors(
            validate({
                ...input,
                [e.target.name]: e.target.value,
            })
        );
    }



    function handleSelect(e) {

        setInput({
            ...input,
            types: Array.from(new Set([...input.types, e.target.value]))
        })

        if (input.name === '' || input.hp === '' || input.attack === '' || input.defense === '' || input.height === '' || input.weight === '' || input.types.length < 0) {
            setButton(true)
        } else {
            setButton(false)
        }

    }





    function handleSubmit(e) {
        e.preventDefault();
        if (pokemons.find((poke) => poke.name.toLowerCase() === input.name.toLowerCase().trim())) {
            alert("Ya existe el Pokemon");
            setErrors({
                ...input,
                [e.target.name]: "PokÃ©mon duplicated",
            });
            history.push('/home')
        } else {
            dispatch(postPokemon(input));
            alert('Pokemon Created');
            setInput({
                name: "", hp: "", attack: "", defense: "", speed: "", height: "", weight: "", types: []
            });
            history.push('/home');
        }


    }

    function handleDelete(e) {
        e.preventDefault();
        setTemps(types.filter((temp) => temp !== e.target.value));
        console.log(types);
        console.log(e.target.value);
    }



    return (
        <div >

            <h1 >CreÃ¡ tu propio Pokemon</h1>
            <form onSubmit={handleSubmit}>
                <label>Nombre: </label>
                <input
                    type='text'
                    value={input.name}
                    name='name'
                    placeholder='Name...'
                    onChange={handleOnChange}

                />
                {errors.name && (<p >{errors.name}</p>)}
                <label c>HP: </label>
                <input
                    type='number'
                    value={input.hp}
                    name='hp'
                    placeholder='HP...'
                    onChange={handleOnChange}

                />
                {errors.hp && (<p>{errors.hp}</p>)}
                <label >Ataque: </label>
                <input
                    type='number'
                    value={input.attack}
                    name='attack'
                    placeholder='Attack...'
                    onChange={handleOnChange}

                />
                {errors.attack && (<p >{errors.attack}</p>)}
                <label >Defensa: </label>
                <input
                    type='number'
                    value={input.defense}
                    name='defense'
                    placeholder='Defense...'
                    onChange={handleOnChange}

                />
                {errors.defense && (<p >{errors.defense}</p>)}
                <label >Velocidad: </label>
                <input
                    type='number'
                    value={input.speed}
                    name='speed'
                    placeholder='Speed...'
                    onChange={handleOnChange}

                />
                {errors.speed && (<p>{errors.speed}</p>)}
                <label>Altura: </label>
                <input
                    type='number'
                    value={input.height}
                    name='height'
                    placeholder='Height...'
                    onChange={handleOnChange}

                />
                {errors.height && (<p>{errors.height}</p>)}
                <label >Peso: </label>
                <input
                    type='number'
                    value={input.weight}
                    name='weight'
                    placeholder='Peso...'
                    onChange={handleOnChange}

                />
                {errors.weight && (<p >{errors.weight}</p>)}
                <select value='DEFAULT' onChange={(e) => handleSelect(e)} >
                    <option value='DEFAULT' disabled>Tipos</option>
                    {
                        types?.map(t => {
                            return (
                                <option value={t.name} key={t.name}>
                                    {t.name}
                                </option>
                            )
                        })
                    }
                </select>
                {/*errors.types && (<p className={style.error}>{errors.types}</p>)*/}
                {/*<ul ><li>{input.types?.map(e => e + ' ')}</li></ul>*/}
                {types.map((typ, id) => {

                    return (
                        <React.Fragment key={id}>
                            <div>
                                {typ}
                                <button
                                    value={typ}
                                    onClick={(e) => handleDelete(e)}
                                >
                                    x
                                </button>
                            </div>
                        </React.Fragment>
                    );
                })}
                <Link to='/home'>
                    <button type='submit' name='submit'>Crear</button>
                </Link>
                <Link to='/home'>
                    <button >Volver</button>
                </Link>
            </form>
        </div>
    )
}

export default CreatePokemon;
