import React from "react";
import { useEffect } from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getDetail } from "../actions";
import { useParams } from "react-router-dom";
import Loading from "./Loading";
import '../css/CardDetail.css';


export default function Detail() {
  const { id } = useParams();
  const dispatch = useDispatch();
  const detailPokemon = useSelector((state) => state.pokemons);
  console.log(detailPokemon)


  useEffect(() => {
    dispatch(getDetail(id)); //accedo al id pasandole props a mi componente Detail
  }, [dispatch]);

   // me traigo el estado detail desde el reducer con useSelector
//   if(!detailPokemon.name){
        
//     return(
//        <Loading/>
//     )

// }else{
    
    return(
        <div >
            {
                    detailPokemon?.map((c) => {
                        return (
                            <React.Fragment>

                                <div>
                                    <div >
                                        <Link  to={'/pokemons/' + c.id}>
                                            <div  >
                                                <Card name={c.name} image={c.image}
                                                    types={
                                                        c.types.map((c) => c.name)} key={c.id} />
                                            </div>
                                        </Link>
                                    </div>
                                </div>

                            </React.Fragment>
                        )
                    })
                }

            </div>
        
    )
}

