import React from "react";
import { useEffect } from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getDetail } from "../actions";
import { useParams } from "react-router-dom";
import Loading from "./Loading";
import '../css/CardDetail.css';


export default function Detail() {
  const { id } = useParams();
  const dispatch = useDispatch();
  const detailPokemon = useSelector((state) => state.pokemons);
  console.log(detailPokemon)


  useEffect(() => {
    dispatch(getDetail(id)); //accedo al id pasandole props a mi componente Detail
  }, [dispatch]);

   // me traigo el estado detail desde el reducer con useSelector
//   if(!detailPokemon.name){
        
//     return(
//        <Loading/>
//     )

// }else{
    
    return(
        <div >
            
            <Link to='/home'>
                <button> Volver </button>
              </Link>
            <div >
                <div >
                    <h1 >{detailPokemon.name}</h1>
                    <div >
                        <h3 >#{detailPokemon.id}</h3>
                    </div>
                    <div>
                        <h4 >HP: {detailPokemon.hp}</h4>
                    </div>
                    <div >
                        <img src={detailPokemon.imageDetail}  alt="img"/>
                    </div>
                    <div >
                        {
                            detailPokemon.types?.map((e,i)=>{
                                return(
                                    <h4 key={detailPokemon.types[i]}>
                                    {detailPokemon.types[i]}
                                    </h4>
                                )
                            })
                        }
                    </div>
                    <div ></div>
                    <div >
                        <h3 >Attack:</h3>
                        <h4 >-{detailPokemon.attack}-</h4>
                        <h3 >Defense:</h3>
                        <h4>-{detailPokemon.defense}-</h4>
                        <h3>Speed:</h3>
                        <h4>-{detailPokemon.speed}-</h4>
                        <h3>Height:</h3>
                        <h4>-{detailPokemon.height}-</h4>
                        <h3>Weight:</h3>
                        <h4>-{detailPokemon.weight}-</h4>
                    </div>
                </div>
            </div>
            
        </div>
        
    )
}

