import React from "react";
import { useEffect } from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { dogDetail } from "../actions";
import { useParams } from "react-router-dom";
import '../css/CardDetails.css';


export default function Detail(props) {
  console.log(props);
  const { id } = useParams();
  const dispatch = useDispatch();


  useEffect(() => {
    dispatch(dogDetail(id)); //accedo al id pasandole props a mi componente Detail
  }, [dispatch]);

  const detailPokemon = useSelector((state) => state.detail); // me traigo el estado detail desde el reducer con useSelector

  return (

    <div  >
      <div >
        {detailPokemon.length > 0 ? (
          <div >
            <h1 >{detailPokemon[0].name} </h1>
            <img 
              src={detailPokemon[0].imageDetail}
              alt='Img not found'
            />
            <p>
              {" "}
              Tipo:{" "}
              {!detailPokemon[0].createIndb
                ? detailPokemon[0].type + " "
                : detailPokemon[0].types.map((el) => el.name + " ")}
            </p>
            <p > Peso: {detailPokemon[0].height} Cm</p>
            <p> Altura: {detailPokemon[0].weight} Kg </p>
            <p>
              {" "}
              AÃ±os de vida:{" "}
              {detailPokemon[0].createIndb
                ? detailPokemon[0].life_span + "years"
                : detailPokemon[0].life_span}{" "}
            </p>
            <div>
              <Link to='/home'>
                <button> Volver </button>
              </Link>
            </div>
          </div>
        ) : (
          <p > Loading...</p>
        )}
      </div>
    </div>
  );
}
